module("Data adapters - Maximum input length");var MaximumInputLength=require("select2/data/maximumInputLength");var $=require("jquery");var Options=require("select2/options");var Utils=require("select2/utils");function MaximumInputStub(){this.called=false}MaximumInputStub.prototype.query=function(d,c){this.called=true};var MaximumInputData=Utils.Decorate(MaximumInputStub,MaximumInputLength);test("0 never displays the notice",function(e){var d=new Options({maximumInputLength:0});var f=new MaximumInputData(null,d);f.trigger=function(){e.ok(false,"No events should be triggered")};f.query({term:""});e.ok(f.called);f=new MaximumInputData(null,d);f.query({term:"test"});e.ok(f.called)});test("< 0 never displays the notice",function(e){var d=new Options({maximumInputLength:-1});var f=new MaximumInputData(null,d);f.trigger=function(){e.ok(false,"No events should be triggered")};f.query({term:""});e.ok(f.called);f=new MaximumInputData(null,d);f.query({term:"test"});e.ok(f.called)});test("triggers when input is too long",function(e){var d=new Options({maximumInputLength:1});var f=new MaximumInputData(null,d);f.trigger=function(){e.ok(true,"The event should be triggered.")};f.query({term:"no"});e.ok(!f.called,"The adapter should not be called")});test("does not trigger when equal",function(e){var d=new Options({maximumInputLength:10});var f=new MaximumInputData(null,d);f.trigger=function(){e.ok(false,"The event should not be triggered.")};f.query({term:"1234567890"});e.ok(f.called)});test("does not trigger when less",function(e){var d=new Options({maximumInputLength:10});var f=new MaximumInputData(null,d);f.trigger=function(){e.ok(false,"The event should not be triggered.")};f.query({term:"123"});e.ok(f.called)});test("works with null term",function(e){var d=new Options({maximumInputLength:1});var f=new MaximumInputData(null,d);f.trigger=function(){e.ok(false,"The event should not be triggered")};f.query({});e.ok(f.called)});