module("Data adapters - Minimum input length");var MinimumInputLength=require("select2/data/minimumInputLength");var $=require("jquery");var Options=require("select2/options");var Utils=require("select2/utils");function StubData(){this.called=false}StubData.prototype.query=function(d,c){this.called=true};var MinimumData=Utils.Decorate(StubData,MinimumInputLength);test("0 never displays the notice",function(e){var d=new Options({minimumInputLength:0});var f=new MinimumData(null,d);f.trigger=function(){e.ok(false,"No events should be triggered")};f.query({term:""});e.ok(f.called);f=new MinimumData(null,d);f.query({term:"test"});e.ok(f.called)});test("< 0 never displays the notice",function(e){var d=new Options({minimumInputLength:-1});var f=new MinimumData(null,d);f.trigger=function(){e.ok(false,"No events should be triggered")};f.query({term:""});e.ok(f.called);f=new MinimumData(null,d);f.query({term:"test"});e.ok(f.called)});test("triggers when input is not long enough",function(e){var d=new Options({minimumInputLength:10});var f=new MinimumData(null,d);f.trigger=function(){e.ok(true,"The event should be triggered.")};f.query({term:"no"});e.ok(!f.called)});test("does not trigger when equal",function(e){var d=new Options({minimumInputLength:10});var f=new MinimumData(null,d);f.trigger=function(){e.ok(false,"The event should not be triggered.")};f.query({term:"1234567890"});e.ok(f.called)});test("does not trigger when greater",function(e){var d=new Options({minimumInputLength:10});var f=new MinimumData(null,d);f.trigger=function(){e.ok(false,"The event should not be triggered.")};f.query({term:"12345678901"});e.ok(f.called)});test("works with null term",function(e){var d=new Options({minimumInputLength:1});var f=new MinimumData(null,d);f.trigger=function(){e.ok(true,"The event should be triggered")};f.query({});e.ok(!f.called)});