module("Defaults - Ajax");test("options are merged recursively with default options",function(a){var d=require("select2/defaults");var b=250;var c="http://www.test.com";var e;d.set("ajax--delay",b);e=d.apply({ajax:{url:c}});a.equal(e.ajax.delay,b,"Ajax default options are present on the merged options");a.equal(e.ajax.url,c,"Ajax provided options are present on the merged options");d.reset()});test("more than one default option can be changed via set()",function(a){var d=require("select2/defaults");var b=123;var c="xml";d.set("ajax--delay",b);d.set("ajax--data-type",c);a.equal(d.defaults.ajax.delay,b,"Both ajax.delay and ajax.dataType present in defaults");a.equal(d.defaults.ajax.dataType,c,"Both ajax.delay and ajax.dataType present in defaults");d.reset()});