module("Defaults - Ajax");test("options are merged recursively with default options",function(g){var i=require("select2/defaults");var f=250;var j="http://www.test.com";var h;i.set("ajax--delay",f);h=i.apply({ajax:{url:j}});g.equal(h.ajax.delay,f,"Ajax default options are present on the merged options");g.equal(h.ajax.url,j,"Ajax provided options are present on the merged options");i.reset()});test("more than one default option can be changed via set()",function(f){var g=require("select2/defaults");var e=123;var h="xml";g.set("ajax--delay",e);g.set("ajax--data-type",h);f.equal(g.defaults.ajax.delay,e,"Both ajax.delay and ajax.dataType present in defaults");f.equal(g.defaults.ajax.dataType,h,"Both ajax.delay and ajax.dataType present in defaults");g.reset()});