module("Results - option");var $=require("jquery");var Options=require("select2/options");var Results=require("select2/results");test("disabled property on option is respected - enabled",function(a){var b=new Results($("<select></select>"),new Options({}));var d=$("<option></option>");var c=b.option({element:d[0]});a.notEqual(c.getAttribute("aria-disabled"),"true")});test("disabled property on option is respected - disabled",function(a){var b=new Results($("<select></select>"),new Options({}));var d=$("<option disabled></option>");var c=b.option({element:d[0]});a.equal(c.getAttribute("aria-disabled"),"true")});test("disabled property on enabled optgroup is respected",function(a){var b=new Results($("<select></select>"),new Options({}));var d=$("<optgroup></optgroup>");var c=b.option({element:d[0]});a.notEqual(c.getAttribute("aria-disabled"),"true")});test("disabled property on disabled optgroup is respected",function(a){var b=new Results($("<select></select>"),new Options({}));var d=$("<optgroup disabled></optgroup>");var c=b.option({element:d[0]});a.equal(c.getAttribute("aria-disabled"),"true")});test("option in disabled optgroup is disabled",function(a){var b=new Results($("<select></select>"),new Options({}));var d=$("<optgroup disabled><option></option></optgroup>").find("option");var c=b.option({element:d[0]});a.equal(c.getAttribute("aria-disabled"),"true")});